// TVComponent.jsx

import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { tvs } from "../../utils/tvDummy";
import { TvContainer, TvDetail, TvInfo, Container, TvImage } from "./TV.style.jsx";

const IMG_BASE_URL = "https://image.tmdb.org/t/p/w500";

const Tv = () => {
  const navigate = useNavigate();
  const [hoveredIndex, setHoveredIndex] = useState(null);

  const handleTvClick = (title, posterPath, voteAverage, overview) => {
    navigate(`/tv/${title}`, {
      state: {
        posterPath,
        voteAverage,
        overview,
      },
    });
  };

function TV() {
  return (
    <div>
      <Container>
        {tvs.results.map((tv, index) => (
          <TvContainer
            key={index}
            onMouseEnter={() => setHoveredIndex(index)}
            onMouseLeave={() => setHoveredIndex(null)}
            onClick={() =>
              handleTvClick(
                tv.title,
                `${IMG_BASE_URL}${tv.poster_path}`,
                tv.vote_average,
                tv.overview
              )
            }
          >
            <TvImage
              src={`${IMG_BASE_URL}${tv.poster_path}`}
              alt={tv.title}
            />
            {hoveredIndex === index && (
              <TvDetail>
                <h4>{tv.title}</h4>
                <p>{tv.overview}</p>
              </TvDetail>
            )}

            <TvInfo>
              <p id="tvTitle" style={{ paddingLeft: "0.5vw" }}>
                {tv.title}
              </p>
              <p id="tvVote" style={{ paddingRight: "0.5vw" }}>
                {tv.vote_average}
              </p>
            </TvInfo>
          </TvContainer>
        ))}
      </Container>
    </div>
  );
}
};

export default Tv;
