import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { movies } from '../movieDummy';

const Container = styled.div`
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    gap: 0.8%;
`;

const MovieContainer = styled.div`
    position: relative;
    width: 250px;
    margin: 16px;
    background-color: #373b69;
    color: white;
    border-radius: 5px;
    box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.1);
    cursor: pointer;

    &:hover {
        .MovieInfo {
            opacity: 1;
        }
    }
`;

const MovieImage = styled.img`
    width: 100%;
    min-height: 15vw;
    margin-top: 0.5vw;
`;

const MovieInfo = styled.div`
    width: 100%;
    min-height: 5%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    color: white;
    font-size: 0.8vw;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, 0.7);
    opacity: 0;
    transition: opacity 0.3s ease;
    padding: 10px;
    cursor: pointer;
`;

const IMG_BASE_URL = "https://image.tmdb.org/t/p/w500";

const Movie = () => {
    const navigate = useNavigate();

    const handleMovieClick = (title, posterPath, voteAverage, overview) => {
        navigate(`/movie/${title}`, {
            state: {
                posterPath,
                voteAverage,
                overview,
            }
        });
    };

    return (
        <Container>
            {movies.results.map((movie, index) => (
                <MovieContainer
                    key={index}
                    onClick={() => handleMovieClick(movie.title, `${IMG_BASE_URL}${movie.poster_path}`, 
                    movie.vote_average, movie.overview)}
                >
                    <MovieImage src={`${IMG_BASE_URL}${movie.poster_path}`} alt={movie.title} />

                    <MovieInfo className="MovieInfo">
                        <p id="movieTitle" style={{ paddingLeft: "0.5vw", margin: 0 }}>{movie.title}</p>
                        <p id="movieOverview" style={{ paddingRight: "0.5vw", margin: 0 }}>{movie.overview}</p>
                    </MovieInfo>

                </MovieContainer>
            ))}
        </Container>
    );
};

export default Movie;
