import React, { useState } from "react";

const LoginPage = () => {
  // 입력된 아이디와 비밀번호를 관리하는 상태
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  // 로그인 버튼을 클릭할 때 실행되는 함수
  const handleLogin = () => {
    // 여기에서 입력된 아이디(username)와 비밀번호(password)를 사용하여 로그인 처리를 할 수 있습니다.
    // 현재는 콘솔에 출력하는 예제 코드를 작성했습니다.
    console.log("Username:", username);
    console.log("Password:", password);

    // 실제 로그인 처리를 추가하려면 여기에 필요한 로직을 구현하세요.
  };

  return (
    <div>
      <h2>Login Page</h2>
      <form>
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <br />
        <button type="button" onClick={handleLogin}>
          Login
        </button>
      </form>
    </div>
  );
};

export default LoginPage;
