import React from "react";

import { useParams } from 'react-router-dom';
const MovieDetail = () => {
  // useParams 훅을 사용하여 동적 부분의 값을 가져옵니다
  const { title } = useParams();

  // title 값에 따라 다른 동작을 수행
  // 예를 들어, title을 기반으로 영화 정보를 불러오거나 특정 컴포넌트를 렌더링할 수 있습니다
  // 이 부분에서 필요한 동작을 수행하도록 코드를 작성합니다
  return (
    <div>
      <div>MovieDetail</div>
      <h1>Movie Detail Page for {title}</h1>
      {/* 기타 컴포넌트 내용 */}
    </div>
  );
};

export default MovieDetail;
