import React from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import { movies } from "../movieDummy";

const Container = styled.div`
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  gap: 0.8%;
`;

const MovieContainer = styled.div`
  position: relative;
  width: 250px;
  margin: 16px;
  background-color: #373b69;
  color: white;
  border-radius: 5px;
  box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.1);
  cursor: pointer;
`;

const MovieImage = styled.img`
  width: 100%;
  min-height: 15vw;
  margin-top: 0.5vw;
`;

const MovieInfo = styled.div`
  width: 100%;
  min-height: 5%;
  display: flex;
  flex-direction: column;  // 변경: 세로 방향으로 나열하도록 수정
  justify-content: space-between;
  color: white;
  font-size: 0.8vw;
  cursor: pointer;

  /* 추가: 마우스 호버 시 나타날 스타일 */
  &:hover {
    background-color: rgba(0, 0, 0, 0.8);
    padding: 1vw;
    border-radius: 5px;
  }

  /* 추가: 기본적으로 숨겨진 상태 */
  #movieOverview {
    display: none;
  }

  /* 추가: 마우스 호버 시 나타날 스타일 */
  &:hover #movieOverview {
    display: block;
  }
`;

const Movie = () => {
  const navigate = useNavigate();

  const handleMovieClick = (title, posterPath, voteAverage, overview) => {
    navigate(`/movie/${title}`, {
      state: {
        posterPath,
        voteAverage,
        overview,
      },
    });
  };

  return (
    <Container>
      {movies.results.map((movie, index) => (
        <MovieContainer
          key={index}
          onClick={() =>
            handleMovieClick(
              movie.title,
              `${IMG_BASE_URL}${movie.poster_path}`,
              movie.vote_average,
              movie.overview
            )
          }
        >
          <MovieImage
            src={`${IMG_BASE_URL}${movie.poster_path}`}
            alt={movie.title}
          />

          <MovieInfo>
            <p id="movieTitle" style={{ paddingLeft: "0.5vw" }}>
              {movie.title}
            </p>
            <p id="movieVote" style={{ paddingRight: "0.5vw" }}>
              {movie.vote_average}
            </p>

            {/* 추가: 마우스 호버 시 나타날 정보 */}
            <p id="movieOverview">{movie.overview}</p>
          </MovieInfo>
        </MovieContainer>
      ))}
    </Container>
  );
};

export default Movie;
